            <div className="allWatches">
        <div className="listWatches">
        <div onClick={event =>  window.location.href='/productDetails'} style={{ height:h}} className="singleWatch">

            <img height={hi} width="50%" src={require('../img/listWatches/1.jpeg')}/>
            <br/><br/>
            <div>image 1</div>
        </div>
        <div onClick={event =>  window.location.href='/productDetails'} style={{ height:h}} className="singleWatch">

